def artcode(s):
    """
    Construit une liste de tuples représentant la chaîne passée en argument

    Args:
        s: chaîne de caractères

    Returns:
        Liste de tuples (caractère, nombre d'occurences consécutives)

    >>> l = artcode('MMMMaaacXolloMM')
    >>> isinstance(l, list)
    True
    >>> len(l)
    8
    >>> l[0]
    ('M', 4)
    >>> l[-1]
    ('M', 2)
    >>> l
    [('M', 4), ('a', 3), ('c', 1), ('X', 1), ('o', 1), ('l', 2), ('o', 1), ('M', 2)]
    """
    # votre code ici

    # initalisation
    # pour chaque caractère de la chaine
    #   si c'est le même que le précédent, on incrémente le compteur
    #   sinon :
    #       on ajoute un tuple à la liste
    #       on modifie le caractère de comparaison
    #       on réinitalise le compteur
    # fin du processus
    list_char = []
    a=0
    prec_char = s[0]
    s += '0'
    for char in s :
        if char == prec_char : 
            a+=1
        elif char == '0':
            list_char.append((prec_char,a))
        else : 
            list_char.append((prec_char,a))
            prec_char = char
            a=1
        
    return list_char

def artdecode(l):
    """
    Construit une chaîne de caractères à partir de la liste de 
    tuples passée en argument

    Args:
        l: liste de tuples (caractère, nombre d'occurences consécutives)

    Returns:
        Chaîne de caractères

    >>> s = artdecode([('M', 4), ('a', 3), ('c', 1), ('X', 1), ('o', 1), ('l', 2), ('o', 1), ('M', 2)])
    >>> isinstance(s, str)
    True
    >>> len(s)
    15
    >>> s[0]
    'M'
    >>> s[3]
    'M'
    >>> s[4]
    'a'
    >>> s[7]
    'c'
    >>> s[8]
    'X'
    >>> s[9]
    'o'
    >>> s[10]
    'l'
    >>> s[12]
    'o'
    >>> s[-1]
    'M'
    >>> s
    'MMMMaaacXolloMM'
    """
    # votre code ici
    # pour chaque tuple de la liste
    #   on allonge la chaine de caractères
    s=""
    for i in l :
        s += i[0]*i[1]

    return s



